mapper.select:
select exp.*, exc.* from oa_express exp inner join oa_expresscompany exc on exp.EXCID=exc.EXCID 

  
  exp.EXPARRIVED = #{isArrived}
  
  
  exc.EXCNAME = #{expressCompany.name}

select * from oa_express where
  
  EXPARRIVED = #{isArrived}
  
limit #{start}, #{rows}

